import React, { memo, useMemo, useState } from "react";
import { Search as SearchIcon, Menu } from "@mui/icons-material";

// Hooks
import useMedia from "../../hooks/useMedia";

// Styles
import {
  Container,
  Title,
  Search,
  NavLinkStyled,
  LeftContainer,
  RightContainer,
} from "./styles";

declare interface SearchBarProps {
  onChangeSearch: (e: string) => void;
  className?: string;
}

const SearchBar = ({ className, onChangeSearch }: SearchBarProps) => {
  const media = useMedia();
  const [openDrawer, setOpenDrawer] = useState(false);

  const title = useMemo(() => {
    return media != "mobile" && <Title>{"Github"}</Title>;
  }, [media]);

  const menuIcon = useMemo(() => {
    return (
      media === "mobile" && (
        <div onClick={() => setOpenDrawer(!openDrawer)}>
          <Menu color="secondary" />
        </div>
      )
    );
  }, [media]);

  const rightContainer = useMemo(() => {
    return (
      media != "mobile" && (
        <RightContainer>
          <NavLinkStyled to="/">
            <span>{"Users"}</span>
          </NavLinkStyled>
          <NavLinkStyled to="repositories">
            <span>{"Repositories"}</span>
          </NavLinkStyled>
        </RightContainer>
      )
    );
  }, [media]);

  return (
    <Container className={className}>
      <LeftContainer>
        {title}
        {menuIcon}
        <Search
          color={"secondary"}
          startAdornment={<SearchIcon color="secondary" />}
          onChange={(e: React.ChangeEvent<HTMLInputElement>) =>
            onChangeSearch(e.target.value)
          }
          media={media}
        />
      </LeftContainer>
      {rightContainer}
    </Container>
  );
};

export default memo(SearchBar);
