import React, { memo, useMemo, createRef } from "react";
import { v4 as uuidv4 } from "uuid";
import {
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
} from "@mui/material";

import { Container, CircularProgressStyled } from "./styles";

import { TableProps, Data } from "./types";

const TableBar = ({ className, header, data, isFetching }: TableProps) => {
  const dataTable = createRef<Table>();

  const onScroll = useMemo(() => {
    const tableEl = dataTable.current;
    if (tableEl.scrollTop === tableEl.scrollHeight - tableEl.offsetHeight) {
      if (list.itemsDisplayed + 10 <= items.length) {
        setList({
          itemsDisplayed: list.itemsDisplayed + 10,
          data: items.slice(0, list.itemsDisplayed + 10),
        });
      }
    }
  }, []);

  return (
    <Container className={className}>
      <TableContainer component={Paper}>
        <Table ref={dataTable} onScroll={onScroll}>
          <TableHead>
            <TableRow>
              {header.map((hd: Data) => (
                <TableCell key={`${uuidv4()}`} align={hd.align}>
                  {hd.value}
                </TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {data.map((row) => (
              <TableRow key={row.id}>
                {row.data.map((r: Data) => (
                  <TableCell key={`${uuidv4()}`} align={r.align}>
                    {r.value}
                  </TableCell>
                ))}
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      {isFetching && <CircularProgressStyled color="primary" size={30} />}
    </Container>
  );
};

export default memo(TableBar);
