import React, { memo, useState, useMemo, useEffect, useCallback } from "react";
import debounce from "lodash.debounce";
import { useLazyQuery } from "@apollo/client";

//Components
import SearchBar from "./../../components/SearchBar/SearchBar";
import Table from "./../../components/Table/Table";

//Types
import { AlignEnum } from "./../../components/Table/types";

//Graphql
import {
  SearchQueryUsersProps,
  SearchQueryUsersNodeProps,
  searchQueryUsers,
} from "../../graphql/query";

//Styles
import { TitleStyled, Container } from "./styles";

const UserSearch = () => {
  const [result, setResult] = useState<string>("");
  const [resultDebounce, setResultDebounce] = useState<string>("");
  const [paginatedData, setPaginatedData] = useState<SearchQueryUsersProps>({});

  const [getUsers, { data, fetchMore, error }] =
    useLazyQuery<SearchQueryUsersProps>(searchQueryUsers);

  useEffect(() => {
    setPaginatedData({
      search: {
        ...data?.search,
        nodes: {
          ...(paginatedData.search?.nodes || []),
          ...data?.search?.nodes,
        },
      },
    });
  }, [data?.search, paginatedData.search?.nodes]);

  useEffect(() => {
    debounce(() => setResultDebounce(result), 500)();
  }, [result, resultDebounce]);

  useEffect(() => {
    getUsers({
      variables: {
        query: resultDebounce,
        type: "USER",
        first: 20,
      },
    });
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [resultDebounce, getUsers]);

  const parsedData = useMemo(
    () =>
      data?.search?.nodes?.map((node: SearchQueryUsersNodeProps) => {
        return {
          id: node.id,
          data:
            [
              {
                value: node.name,
                align: AlignEnum.left,
              },
              {
                value: node.email,
                align: AlignEnum.left,
              },
              {
                value: node.location,
                align: AlignEnum.left,
              },
              {
                value: node.url,
                align: AlignEnum.left,
              },
            ] || [],
        };
      }) || [],
    [data]
  );

  const fetchUsers = useCallback(() => {
    fetchMore({
      variables: {
        query: resultDebounce,
        type: "USER",
        after: data?.search?.pageInfo.endCursor,
        first: 40,
      },
    });
  }, [data?.search?.pageInfo.endCursor, fetchMore, resultDebounce]);

  return (
    <>
      <SearchBar onChangeSearch={setResult} />
      <Container>
        <TitleStyled text="Users" />
        <Table
          header={[
            {
              value: "Name",
              align: AlignEnum.left,
            },
            {
              value: "Email",
              align: AlignEnum.left,
            },
            {
              value: "Location",
              align: AlignEnum.left,
            },
            {
              value: "Url",
              align: AlignEnum.left,
            },
          ]}
          data={parsedData}
          nextPage={fetchUsers}
          hasMore={data?.search?.pageInfo.hasNextPage || false}
        />
      </Container>
    </>
  );
};

export default memo(UserSearch);
