import React, { memo, useState, useMemo, useEffect } from 'react';
import { useLazyQuery, useQuery } from '@apollo/client';

//Graphql
import { SearchQueryRepositoryProps, SearchQueryRepositoryNodeProps, searchQueryRepository } from '../../graphql/query';

//Components
import SearchBar from './../../components/SearchBar/SearchBar';
import Table from './../../components/Table/Table';

//Types
import { AlignEnum } from '../../components/Table/types';

const RepositoriesSearch = () => {
  const [result, setResult] = useState<string>();

  const [getRepositories, { data, loading }] = useLazyQuery<SearchQueryRepositoryProps>(searchQueryRepository);
  console.log(data, loading);

  useEffect(() => {
    getRepositories({
      variables: {
        query: result,
        type: "REPOSITORY",
        after: data?.search?.pageInfo.startCursor,
        first: 2
      }
    })
  }, [result])

  const parsedData = useMemo(() => data?.search?.nodes?.map(
    (node: SearchQueryRepositoryNodeProps) => (
      {
        id: node.id,
        data: [
          {
            value: node.name,
            align: AlignEnum.left,
          },
          {
            value: node.url,
            align: AlignEnum.left,
          },
          {
            value: node.description,
            align: AlignEnum.left,
          },
          {
            value: `${node.updatedAt.getDate()}/${node.updatedAt.getMonth()}/${node.updatedAt.getFullYear()}`,
            align: AlignEnum.center,
          },
          {
            value: `${node.createdAt.getDate()}/${node.createdAt.getMonth()}/${node.createdAt.getFullYear()}`,
            align: AlignEnum.center,
          },
        ] || []
      }
    )) || [], [data]);

  return (
    <>
      <SearchBar onChangeSearch={setResult} />
      <Table header={
        [
          {
            value: 'Name',
            align: AlignEnum.left,
          },
          {
            value: 'Url',
            align: AlignEnum.left,
          },
          {
            value: 'Description',
            align: AlignEnum.left,
          },
          {
            value: 'Updated',
            align: AlignEnum.left,
          },
          {
            value: 'Created',
            align: AlignEnum.left,
          }
        ]
      }
        data={parsedData}
        isFetching={loading}
      />
    </>
  )
}

export default memo(RepositoriesSearch);
