import React, { memo } from "react";
import { v4 as uuidv4 } from "uuid";
import InfiniteScroll from "react-infinite-scroll-component";
import {
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
} from "@mui/material";

import { CircularProgressStyled, Container } from "./styles";

import { TableProps, Data } from "./types";

const Table = ({ className, header, data, isFetching }: TableProps) => {
  return (
    <Container className={className}>
      <InfiniteScroll>
        <TableContainer component={Paper}>
          <Table onScroll={onScroll}>
            <TableHead>
              <TableRow>
                {header.map((hd: Data) => (
                  <TableCell key={`${uuidv4()}`} align={hd.align}>
                    {hd.value}
                  </TableCell>
                ))}
              </TableRow>
            </TableHead>
            <TableBody>
              {data.map((row) => (
                <TableRow key={row.id}>
                  {row.data.map((r: Data) => (
                    <TableCell key={`${uuidv4()}`} align={r.align}>
                      {r.value}
                    </TableCell>
                  ))}
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
        {isFetching && <CircularProgressStyled color="primary" size={30} />}
      </InfiniteScroll>
    </Container>
  );
};

export default memo(Table);
