import React, { memo, useState, useMemo, useEffect } from "react";
import debounce from "lodash.debounce";
import { useLazyQuery } from "@apollo/client";

//Components
import SearchBar from "./../../components/SearchBar/SearchBar";
import Table from "./../../components/Table/Table";

//Graphql
import {
  SearchQueryUsersProps,
  SearchQueryUsersNodeProps,
  searchQueryUsers,
} from "../../graphql/query";

//Types
import { AlignEnum } from "../../components/Table/types";

const UserSearch = () => {
  const [result, setResult] = useState<string>();
  const [resultDebounce, setResultDebounce] = useState<string>();

  const [getUsers, { data, loading }] =
    useLazyQuery<SearchQueryUsersProps>(searchQueryUsers);

  useEffect(() => {
    debounce(() => setResultDebounce(result), 500)();
  }, [result]);

  const cursor = useMemo(() => data?.search?.pageInfo.endCursor, [resultDebounce])
  useEffect(() => {
    getUsers({
      variables: {
        query: resultDebounce,
        type: "USER",
        after: cursor,
        first: 2,
      },
    });
  }, [resultDebounce, getUsers]);

  const parsedData = useMemo(
    () =>
      data?.search?.nodes?.map((node: SearchQueryUsersNodeProps) => {
        return {
          id: node.id,
          data:
            [
              {
                value: node.name,
                align: AlignEnum.left,
              },
              {
                value: node.email,
                align: AlignEnum.left,
              },
              {
                value: node.location,
                align: AlignEnum.left,
              },
              {
                value: node.url,
                align: AlignEnum.center,
              },
            ] || [],
        };
      }) || [],
    [data]
  );

  return (
    <>
      <SearchBar onChangeSearch={setResult} />
      <Table
        header={[
          {
            value: "Name",
            align: AlignEnum.left,
          },
          {
            value: "Email",
            align: AlignEnum.left,
          },
          {
            value: "Location",
            align: AlignEnum.left,
          },
          {
            value: "Url",
            align: AlignEnum.left,
          },
        ]}
        data={parsedData}
        isFetching={loading}
      />
    </>
  );
};

export default memo(UserSearch);
