import React, { memo, createRef, useCallback } from "react";
import { v4 as uuidv4 } from "uuid";
import {
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
} from "@mui/material";

import { Container, CircularProgressStyled } from "./styles";

import { TableProps, Data } from "./types";

const TableBar = ({ className, header, data, isFetching }: TableProps) => {
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  const dataTable = createRef<any>();

  const onScroll = useCallback(() => {
    console.log(dataTable?.current);
    const tableEl = dataTable.current;
    if (tableEl.scrollTop === tableEl.scrollHeight - tableEl.offsetHeight) {
      console.log("fetch");
    }
  }, [dataTable]);

  return (
    <Container ref={dataTable} className={className}>
      <TableContainer component={Paper}>
        <Table onScroll={onScroll}>
          <TableHead>
            <TableRow>
              {header.map((hd: Data) => (
                <TableCell key={`${uuidv4()}`} align={hd.align}>
                  {hd.value}
                </TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {data.map((row) => (
              <TableRow key={row.id}>
                {row.data.map((r: Data) => (
                  <TableCell key={`${uuidv4()}`} align={r.align}>
                    {r.value}
                  </TableCell>
                ))}
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      {isFetching && <CircularProgressStyled color="primary" size={30} />}
    </Container>
  );
};

export default memo(TableBar);
